mapper.select:
select m.m_title movieName, count(r.res_num)*avg(m.m_price) sales from reservations r join movies m on r.m_num = m.m_num
where r.res_date between #{timeStart} and #{timeEnd}
where r.res_date between add_months(sysdate,-3) and sysdate
and m.m_title = #{movieName}
group by m.m_title order by m.m_title

select c.c_n cinemaName, count(r.res_num)*avg(m.m_price) sales from reservations r join movies m on r.m_num = m.m_num join cinemas c on r.c_num = c.c_num
where r.res_date between #{timeStart} and #{timeEnd}
where r.res_date between add_months(sysdate,-3) and sysdate
and c.c_n = #{cinemaName}
group by c.c_n order by c.c_n
